package com.webapptest.model.dao;import java.sql.Connection;import java.sql.PreparedStatement;import java.sql.ResultSet;import java.sql.SQLException;import javax.sql.DataSource;import org.springframework.jdbc.core.RowMapper;import org.springframework.jdbc.core.JdbcTemplate;import java.util.List;import java.util.ArrayList;import com.webapptest.model.entity.Section;import org.springframework.jdbc.core.support.JdbcDaoSupport;import java.util.Map;import org.springframework.jdbc.core.BeanPropertyRowMapper;public class SectionDAO{	private JdbcTemplate jdbcTemplate;	public void setDataSource(DataSource dataSource) {		jdbcTemplate = new JdbcTemplate(dataSource);	}			public List<Section> findAll()	{ 		String sql = "SELECT * FROM SECTIONS";		List<Section> sections = new ArrayList<Section>();		List<Map<String,Object>> rows = jdbcTemplate.queryForList(sql);		for (Map row : rows) 		{			Section section = new Section();			section.setId((Integer)(row.get("id")));			section.setName((String)row.get("name"));			section.setPathToFile((String)row.get("pathToFile"));			sections.add(section);		} 		return sections;	}		public Section findById(int id)	{		String sql = "SELECT * FROM SECTIONS WHERE ID=?";		return (Section)jdbcTemplate.queryForObject(sql,new Object[]{id},new BeanPropertyRowMapper(Section.class));	}		public void addSection(String name,String pathToFile)	{		String sql="INSERT INTO SECTIONS(`NAME`,`PATHTOFILE`) VALUES(?,?)";		jdbcTemplate.update(sql,new Object[]{name,pathToFile});			}}