package com.webapptest.model.entity;import java.util.Date;public class Blog{		private int _id;		private String _author;		private Date _dateOfCreation;		private String _title;		private String _text;				public Blog(){}		public Blog(int id,String author,Date dateOfCreation,String title,String text)		{			_id=id;			_author=author;			_dateOfCreation=dateOfCreation;			_title=title;			_text=text;		}				public void setId(int id)		{			_id=id;		}		public int getId()		{			return _id;		}		public void setAuthor(String author)		{			_author=author;		}		public String getAuthor()		{			return _author;		}		public void setDateOfCreation(Date dateOfCreation)		{			_dateOfCreation=dateOfCreation;		}		public Date getDateOfCreation()		{			return _dateOfCreation;		}		public void setTitle(String title)		{			_title=title;		}		public String getTitle()		{			return _title;		}		public void setText(String text)		{			_text=text;		}		public String getText()		{			return _text;		}		}