package com.webapptest.model.entity;public class Image{	private int _id;	private String _name;	private String _pathToFile;	private int _idOfGroup;	private int _idOfSection;		public Image(){}	public Image(String name,String pathToFile,int idOfGroup,int idOfSection)	{		_name=name;		_pathToFile=pathToFile;		_idOfGroup=idOfGroup;		_idOfSection=idOfSection;			}	public void setId(int id)	{		_id=id;	}	public int getId()	{		return _id;	}	public void setName(String name)	{		_name=name;	}	public String getName()	{		return _name;	}	public void setPathToFile(String pathToFile)	{		_pathToFile=pathToFile;	}	public String getPathToFile()	{		return _pathToFile;	}		public void setIdOfGroup(int idOfGroup)	{		_idOfGroup=idOfGroup;	}	public int getIdOfGroup()	{		return _idOfGroup;	}	public void setIdOfSection(int idOfSection)	{		_idOfSection=idOfSection;	}	public int getIdOfSection()	{		return _idOfSection;	}}